CREATE OR REPLACE FUNCTION obter_primeiro_dia ( nr_seq_tratamento_p bigint , dt_agenda_p timestamp) RETURNS varchar AS $body$
DECLARE


dt_primeiro_dia_w timestamp;
ie_primeiro_dia_w varchar(1);


BEGIN

ie_primeiro_dia_w := 'N';

SELECT MIN(dt_agenda)
INTO STRICT   dt_primeiro_dia_w
FROM rxt_agenda
WHERE nr_seq_tratamento = nr_seq_tratamento_p
and		ie_status_agenda not in ('B','C','F','H');

IF (trunc(dt_primeiro_dia_w) = trunc(dt_agenda_p)) THEN
	ie_primeiro_dia_w := 'S';
END IF;

RETURN ie_primeiro_dia_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
