CREATE OR REPLACE FUNCTION obter_primeiro_hor_sol (hr_prim_horario_p text, dt_inicio_prescr_p timestamp, ie_urgente_p text) RETURNS timestamp AS $body$
DECLARE

ds_retorno_w	timestamp;
hr_fim_w	varchar(10);
hr_inicio_w	varchar(10);


BEGIN

hr_inicio_w	:= hr_prim_horario_p || ':01';
hr_fim_w	:= to_char(dt_inicio_prescr_p,'hh24:mi:ss');

if (hr_inicio_w	> hr_fim_w) or (ie_urgente_p	= 'S') then
	ds_retorno_w := to_date(to_char(dt_inicio_prescr_p, 'dd/mm/yyyy') || to_char(to_date(hr_prim_horario_p, 'hh24:mi'), 'hh24:mi'), 'dd/mm/yyyy hh24:mi');
else
	ds_retorno_w := to_date(to_char(dt_inicio_prescr_p + 1, 'dd/mm/yyyy') || to_char(to_date(hr_prim_horario_p, 'hh24:mi'), 'hh24:mi'), 'dd/mm/yyyy hh24:mi');
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
