CREATE OR REPLACE FUNCTION obter_primeiro_medico (nr_atendimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
crm_medico_w		varchar(20);
cd_medico_w			varchar(10);
ds_medico_w			varchar(200);
ie_troca_medico_w	varchar(1);

/* 
ie_opcao_p 
C - c√≥digo 
N - nome 
R - CRM 
*/
 
 

BEGIN 
 
select	coalesce(max(1),0) 
into STRICT	ie_troca_medico_w 
from	atendimento_troca_medico 
where	nr_atendimento = nr_atendimento_p;
 
if (ie_troca_medico_w = 1) then 
	begin 
	select	cd_medico_anterior, 
			obter_crm_medico(cd_medico_anterior) 
	into STRICT	cd_medico_w, 
			crm_medico_w 
	from	atendimento_troca_medico 
	where	nr_atendimento = nr_atendimento_p 
	and		nr_sequencia = 
	( 
	SELECT	min(nr_sequencia) 
	from	atendimento_troca_medico 
	where	nr_atendimento = nr_atendimento_p 
	);
 
	select	substr(obter_nome_pf(cd_medico_anterior),1,200) 
	into STRICT	ds_medico_w 
	from	atendimento_troca_medico 
	where	nr_atendimento = nr_atendimento_p 
	and		nr_sequencia = 
	( 
	SELECT	min(nr_sequencia) 
	from	atendimento_troca_medico 
	where	nr_atendimento = nr_atendimento_p 
	);
	end;
else 
	begin 
	select	cd_medico_resp, 
			obter_crm_medico(cd_medico_resp) 
	into STRICT	cd_medico_w, 
			crm_medico_w 
	from	atendimento_paciente 
	where	nr_atendimento = nr_atendimento_p;
 
	select	substr(obter_nome_pf(cd_medico_resp),1,200) 
	into STRICT	ds_medico_w 
	from	atendimento_paciente 
	where	nr_atendimento = nr_atendimento_p;
	end;
end if;	
 
if (upper(ie_opcao_p) = 'C') then 
	return	cd_medico_w;
elsif (upper(ie_opcao_p) = 'N') then 
	return	ds_medico_w;
else 
	return	crm_medico_w;	
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
