SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_primeiro_pacote_atend (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


/*Identifica o primeiro pacote gerado no atendimento, mesmo que nÃ£o tenha sido gerado (gatilho)*/

ds_pacote_w	procedimento.ds_procedimento%type;

c01 CURSOR FOR
	SELECT	substr(obter_desc_procedimento(a.cd_procedimento, a.ie_origem_proced),1,255)
	from	procedimento_paciente a,
		conta_paciente b
	where	a.nr_interno_conta = b.nr_interno_conta
	and	a.nr_atendimento = nr_atendimento_p
	and	coalesce(b.ie_cancelamento::text, '') = ''
	and	exists (SELECT	1
			from	pacote x
			where	x.cd_proced_pacote = a.cd_procedimento
			and	x.ie_origem_proced = a.ie_origem_proced
			and	x.cd_convenio = a.cd_convenio
			and	x.cd_estabelecimento = b.cd_estabelecimento
			and	x.ie_situacao = 'A')
	order by	a.nr_sequencia;



BEGIN

if (coalesce(nr_atendimento_p,0) > 0) then

	open c01;
	loop
	fetch c01 into
		ds_pacote_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		ds_pacote_w	:= ds_pacote_w;
		exit;
	end loop;
	close c01;

end if;

return ds_pacote_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_primeiro_pacote_atend (nr_atendimento_p bigint) FROM PUBLIC;

