SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_prim_comp_sol (nr_prescricao_p bigint, nr_seq_solucao_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_comp_w		bigint;
ds_componente_w	varchar(100);


BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_solucao_p IS NOT NULL AND nr_seq_solucao_p::text <> '') then
	/* obter sequencia item */

	select	min(nr_sequencia)
	into STRICT	nr_seq_comp_w
	from	prescr_material
	where	nr_prescricao = nr_prescricao_p
	and	nr_sequencia_solucao = nr_seq_solucao_p;

	/* obter item */

	select	substr(coalesce(b.DS_ABREV_SOLUCAO,obter_desc_material(a.cd_material)),1,100)
	into STRICT	ds_componente_w
	from	prescr_material a,
		material b
	where	a.nr_prescricao = nr_prescricao_p
	and	a.cd_material	= b.cd_material
	and	a.nr_sequencia_solucao = nr_seq_solucao_p
	and	a.nr_sequencia = nr_seq_comp_w;
end if;

return ds_componente_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_prim_comp_sol (nr_prescricao_p bigint, nr_seq_solucao_p bigint) FROM PUBLIC;

