CREATE OR REPLACE FUNCTION obter_prim_horario_cpoe_md (ie_administracao_p text, dt_inicio_p timestamp, dt_inicio_base_p timestamp, hr_prim_horario_p text ) RETURNS varchar AS $body$
DECLARE


     hr_prim_horario_w varchar(10) := hr_prim_horario_p;

BEGIN
    if (ie_administracao_p <> 'P') then
      if (trunc(dt_inicio_p) > trunc(dt_inicio_base_p)) then
        hr_prim_horario_w := '00:00';
      else
        hr_prim_horario_w := to_char(dt_inicio_base_p,'hh24:mi');
      end if;
    end if;

    return hr_prim_horario_w;

END;
$body$
