CREATE OR REPLACE FUNCTION obter_prim_horario_solucao (dt_inicio_presc_p text) RETURNS varchar AS $body$
DECLARE


dt_prim_horario_w	timestamp;
dt_inicio_presc_w	timestamp;


BEGIN
dt_inicio_presc_w	:= to_date(dt_inicio_presc_p,'dd/mm/yyyy hh24:mi:ss');
if (mod((pkg_date_utils.extract_field('HOUR',dt_inicio_presc_w))::numeric ,2) = 0) then
	dt_prim_horario_w	:= PKG_DATE_UTILS.start_of(dt_inicio_presc_w,'hh24',0) + 2/24;
else
	dt_prim_horario_w	:= PKG_DATE_UTILS.start_of(dt_inicio_presc_w,'hh24',0) + 1/24;
end if;

return	to_char(dt_prim_horario_w,'hh24:mi');

end;
$body$
