CREATE OR REPLACE FUNCTION obter_prim_horario_sol_regra (nr_etapas_p bigint, dt_prescricao_p timestamp, hr_prim_horario_p text) RETURNS varchar AS $body$
DECLARE

hr_prim_hor_w	varchar(5) := null;

BEGIN

Select	max(dt_prim_horario)
into STRICT	hr_prim_hor_w
from	regra_horario_solucao
where	coalesce(nr_etapas,coalesce(nr_etapas_p,0)) = coalesce(nr_etapas_p,0)
and 	obter_se_lib_prescr_vig_disp(to_date(hr_inicio, 'hh24:mi'), to_date(hr_fim, 'hh24:mi'), dt_prescricao_p) = 'S';

return	coalesce(hr_prim_hor_w, hr_prim_horario_p);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
