CREATE OR REPLACE FUNCTION obter_prim_hor_interv_agora ( cd_intervalo_p text, dt_sistema_p text) RETURNS varchar AS $body$
DECLARE


qt_adic_w	double precision;


BEGIN

select	coalesce(qt_min_agora,0)/1440
into STRICT	qt_adic_w
from 	intervalo_prescricao
where 	cd_intervalo = cd_intervalo_p;

return	to_char(coalesce(to_date(dt_sistema_p,'dd/mm/yyyy hh24:mi:ss'),clock_timestamp()) + qt_adic_w, 'hh24:mi');

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
