CREATE OR REPLACE FUNCTION obter_prim_hor_medic_interv ( nr_atendimento_p bigint, nr_prescricoes_p text, dt_inicio_prescr_p timestamp, cd_material_p bigint, cd_intervalo_p text, ie_recalc_horario_p text, ie_regra_prim_hor_p text, ie_agora_p text, qt_dose_p bigint) RETURNS timestamp AS $body$
DECLARE


nr_hora_interv_w	double precision;
dt_horario_w		timestamp;
dt_horario_ww		timestamp;
hr_prim_horario_w	timestamp;
dt_inicio_prescr_w	timestamp;
nr_prescricoes_w	varchar(4000);
dt_inicio_w			timestamp;
dt_final_w			timestamp;


BEGIN
nr_prescricoes_w	:= ' ' || nr_prescricoes_p || ' ';
nr_prescricoes_w	:= replace(nr_prescricoes_w, ',',' ');
nr_prescricoes_w	:= replace(nr_prescricoes_w, '  ',' ');

dt_inicio_prescr_w	:= dt_inicio_prescr_p;
dt_inicio_w			:= dt_inicio_prescr_p - 5;
dt_final_w			:= clock_timestamp() + interval '5 days';

if (ie_recalc_horario_p = 'I') or (ie_regra_prim_hor_p = 'H') then
	begin
	if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (dt_inicio_prescr_p IS NOT NULL AND dt_inicio_prescr_p::text <> '') and (cd_material_p IS NOT NULL AND cd_material_p::text <> '') and (cd_intervalo_p IS NOT NULL AND cd_intervalo_p::text <> '') then

		nr_hora_interv_w	:= obter_ocorrencia_intervalo(cd_intervalo_p, 24, 'H');
		dt_horario_w		:= dt_inicio_prescr_p - nr_hora_interv_w / 24;

		select	/*+ INDEX(b PRESMAT_I7) */ max(c.dt_horario)
		into STRICT	dt_horario_ww
		from	prescr_medica a,
				prescr_material b,
				prescr_mat_hor c
		where	1 = 1
		and		c.nr_seq_material	= b.nr_sequencia
		and		c.nr_prescricao		= b.nr_prescricao
		and		coalesce(c.ie_situacao,'A')	= 'A'
		and		c.dt_horario		>= dt_horario_w - 220/24
		and		coalesce(b.dt_suspensao::text, '') = ''
		and		b.ie_agrupador		= 1
		and 	coalesce(c.ie_dose_especial,'N') <> 'S'
		and		b.cd_intervalo		= cd_intervalo_p
		and 	b.qt_dose		= coalesce(qt_dose_p, b.qt_dose)
		and		b.cd_material		= cd_material_p
		and		b.nr_prescricao		= a.nr_prescricao
		and		coalesce(a.dt_suspensao::text, '') = ''
		and		(coalesce(a.dt_liberacao_medico, a.dt_liberacao) IS NOT NULL AND (coalesce(a.dt_liberacao_medico, a.dt_liberacao))::text <> '')
		--and	Obter_se_horario_liberado(c.dt_lib_horario, c.dt_horario) = 'S'
		and		a.dt_inicio_prescr between dt_inicio_w and dt_final_w
		and		a.nr_atendimento	= nr_atendimento_p;
		
		if (dt_horario_ww IS NOT NULL AND dt_horario_ww::text <> '') then
			hr_prim_horario_w	:= dt_horario_ww + nr_hora_interv_w / 24;
		end if;

	end if;
	end;
elsif (ie_recalc_horario_p = 'H') or (ie_regra_prim_hor_p = 'H') then
	begin
	if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (dt_inicio_prescr_p IS NOT NULL AND dt_inicio_prescr_p::text <> '') and (cd_material_p IS NOT NULL AND cd_material_p::text <> '') and (cd_intervalo_p IS NOT NULL AND cd_intervalo_p::text <> '') then

		nr_hora_interv_w	:= obter_ocorrencia_intervalo(cd_intervalo_p, 24, 'H');

		select	min(c.dt_horario)
		into STRICT	dt_horario_ww
		from	prescr_medica a,
				prescr_material b,
				prescr_mat_hor c
		where	c.nr_prescricao		= b.nr_prescricao
		and 	coalesce(c.ie_dose_especial,'N') <> 'S'
		and		c.nr_seq_material	= b.nr_sequencia
		and		coalesce(c.ie_situacao,'A')	= 'A'
		and		coalesce(b.dt_suspensao::text, '') = ''
		and		((ie_agora_p = 'S') or (coalesce(b.ie_urgencia,'N') = 'N'))
		and		b.cd_intervalo		= coalesce(cd_intervalo_p, b.cd_intervalo)
		and 	b.qt_dose		= coalesce(qt_dose_p, b.qt_dose)
		and		b.ie_agrupador		= 1
		and		b.cd_material		= cd_material_p
		and		b.nr_prescricao		= a.nr_prescricao
		and		coalesce(a.dt_suspensao::text, '') = ''
		and		(coalesce(a.dt_liberacao_medico, a.dt_liberacao) IS NOT NULL AND (coalesce(a.dt_liberacao_medico, a.dt_liberacao))::text <> '')
		and		nr_prescricoes_w  like	'% ' || a.nr_prescricao || ' %'
		and		a.dt_inicio_prescr between dt_inicio_w and dt_final_w
		and		a.nr_atendimento	= nr_atendimento_p;
		--and	Obter_se_horario_liberado(c.dt_lib_horario, c.dt_horario) = 'S';
		
		if (dt_horario_ww IS NOT NULL AND dt_horario_ww::text <> '') then
			begin
			while(ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(dt_horario_ww) >= ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(dt_inicio_prescr_p)) loop
				begin
				dt_horario_ww	:= dt_horario_ww - 1;
				end;
			end loop;
			if (nr_hora_interv_w > 0) then
				while(dt_horario_ww < dt_inicio_prescr_p) loop
					begin
					dt_horario_ww	:= dt_horario_ww + (coalesce(nr_hora_interv_w,1) / 24);
					end;
				end loop;
			end if;
			hr_prim_horario_w	:= dt_horario_ww;
			end;
		end if;
	end if;
	end;
elsif (ie_recalc_horario_p = 'U') then
	begin
	if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (dt_inicio_prescr_p IS NOT NULL AND dt_inicio_prescr_p::text <> '') and (cd_material_p IS NOT NULL AND cd_material_p::text <> '') and (cd_intervalo_p IS NOT NULL AND cd_intervalo_p::text <> '') then

		nr_hora_interv_w	:= obter_ocorrencia_intervalo(cd_intervalo_p, 24, 'H');

		select	max(c.dt_horario)
		into STRICT	dt_horario_w
		from	prescr_medica a,
				prescr_material b,
				prescr_mat_hor c
		where	c.nr_prescricao		= b.nr_prescricao
		and		c.nr_seq_material	= b.nr_sequencia
		and		coalesce(c.ie_situacao,'A')	= 'A'
		and 	coalesce(c.ie_dose_especial,'N') <> 'S'
		and		coalesce(b.dt_suspensao::text, '') = ''
		and		b.cd_material		= cd_material_p
		and		b.ie_agrupador		= 1
		and		coalesce(a.dt_suspensao::text, '') = ''
		and		(coalesce(a.dt_liberacao_medico, a.dt_liberacao) IS NOT NULL AND (coalesce(a.dt_liberacao_medico, a.dt_liberacao))::text <> '')
		and		b.nr_prescricao		= a.nr_prescricao
		and		nr_prescricoes_w  like	'% ' || a.nr_prescricao || ' %'
		and		a.dt_inicio_prescr between dt_inicio_w and dt_final_w
		and		a.nr_atendimento	= nr_atendimento_p;
		--and	Obter_se_horario_liberado(c.dt_lib_horario, c.dt_horario) = 'S';
		
		if (dt_horario_w > dt_inicio_prescr_w) then
			dt_inicio_prescr_w	:= dt_horario_w;
		end if;
		
		if (dt_horario_w IS NOT NULL AND dt_horario_w::text <> '') then
			begin	
			
			while(dt_horario_ww < dt_inicio_prescr_w) loop
				begin
				dt_horario_ww	:= dt_horario_ww + (coalesce(nr_hora_interv_w,1) / 24);
				end;
			end loop;
			hr_prim_horario_w	:= dt_horario_ww;
			end;
		end if;
	end if;
	end;
end if;

return hr_prim_horario_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
