CREATE OR REPLACE FUNCTION obter_prim_hor_medic_prescr (nr_prescricao_p bigint) RETURNS timestamp AS $body$
DECLARE


dt_prescricao_w	timestamp;
dt_retorno_w	timestamp;


BEGIN

select	max(dt_prescricao)
into STRICT	dt_prescricao_w
from	prescr_medica
where	nr_prescricao = nr_prescricao_p;

dt_retorno_w := trunc(dt_prescricao_w + (1/24),'hh');

return	dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
