CREATE OR REPLACE FUNCTION obter_prim_hor_pend_lab (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_horario_w	varchar(11);


BEGIN 
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then 
 
	select	to_char(min(dt_horario),'dd/mm hh24:mi') 
	into STRICT	ds_horario_w 
	from	adep_pend_v 
	where	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '') 
	and	dt_validade_prescr > clock_timestamp() 
	and	coalesce(ie_laboratorio,'N') = 'S' 
	and	nr_atendimento = nr_atendimento_p;
 
end if;
 
return ds_horario_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
