CREATE OR REPLACE FUNCTION obter_prim_hor_prescr_regra (dt_prescricao_p timestamp, hr_inicial_p timestamp, hr_final_p timestamp, ie_opcao_p text) RETURNS timestamp AS $body$
DECLARE


hr_inicial_w	timestamp;
hr_final_w		timestamp;
hr_retorno_w	timestamp;


BEGIN

if (dt_prescricao_p IS NOT NULL AND dt_prescricao_p::text <> '') and (hr_inicial_p IS NOT NULL AND hr_inicial_p::text <> '') and (hr_final_p IS NOT NULL AND hr_final_p::text <> '') and (ie_opcao_p IS NOT NULL AND ie_opcao_p::text <> '') then
	
	hr_inicial_w    := ESTABLISHMENT_TIMEZONE_UTILS.dateAtTime(dt_prescricao_p, hr_inicial_p);
	hr_final_w		:= ESTABLISHMENT_TIMEZONE_UTILS.dateAtTime(dt_prescricao_p, hr_final_p);
	
	if (hr_final_w < hr_inicial_w) then
		hr_final_w := hr_final_w + 1;
	end if;

	if (ie_opcao_p = 'I') then
		hr_retorno_w	:= hr_inicial_w;
	elsif (ie_opcao_p = 'F') then
		hr_retorno_w	:=  hr_final_w;
	end if;
end if;

return hr_retorno_w;

end;
$body$
