CREATE OR REPLACE FUNCTION obter_prim_hor_sae ( dt_prescricao_p timestamp, cd_setor_Atendimento_p bigint, nm_usuario_p text) RETURNS timestamp AS $body$
DECLARE

dt_retorno_w	timestamp;
ie_prox_hor_w	varchar(10);
ie_hora_w	varchar(10);
dt_inicio_prescricao_w timestamp;
cd_estabelecimento_w	smallint;


BEGIN

cd_estabelecimento_w := wheb_usuario_pck.get_cd_estabelecimento;

select	max(HR_INICIO_PRESCRICAO_SAE)
into STRICT	dt_inicio_prescricao_w
from	setor_atendimento
where	cd_setor_atendimento	= cd_setor_Atendimento_p;

if (dt_inicio_prescricao_w IS NOT NULL AND dt_inicio_prescricao_w::text <> '') then
	ie_hora_w		:= obter_valor_param_usuario(281,388, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_w);
	ie_prox_hor_w	:= obter_valor_param_usuario(281,782, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_w);
	if (ie_hora_w	= '1') and (dt_inicio_prescricao_w IS NOT NULL AND dt_inicio_prescricao_w::text <> '') then
		dt_retorno_w	:= to_date(to_char(dt_prescricao_p,'dd/mm/yyyy')||' '||to_char(dt_inicio_prescricao_w,'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss');
		if (dt_retorno_w	< clock_timestamp()) and (ie_prox_hor_w	= 'S') then
			dt_retorno_w	:= trunc(dt_prescricao_p,'hh24') + 1/24;
		end if;
	elsif (ie_hora_w	= '2') then
			dt_retorno_w	:= trunc(dt_prescricao_p,'hh24') + 1/24;
	end if;
end if;


return	dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
