CREATE OR REPLACE FUNCTION obter_prim_hor_setor_rep ( cd_setor_atendimento_p bigint) RETURNS timestamp AS $body$
DECLARE


hr_inicio_prescricao_w	timestamp;
dt_prim_horario_w	timestamp;


BEGIN
if (cd_setor_atendimento_p IS NOT NULL AND cd_setor_atendimento_p::text <> '') then
	begin

	select	max(hr_inicio_prescricao)
	into STRICT	hr_inicio_prescricao_w
	from	setor_atendimento
	where	cd_setor_atendimento = cd_setor_atendimento_p;

	if (coalesce(hr_inicio_prescricao_w::text, '') = '') then
		begin
		hr_inicio_prescricao_w	:= to_date('30/12/1899 00:00:00','dd/mm/yyyy hh24:mi:ss');
		end;
	end if;

	if (to_char(hr_inicio_prescricao_w, 'hh24:mi:ss') > to_char(clock_timestamp(), 'hh24:mi:ss')) then
		dt_prim_horario_w := to_date(to_char(clock_timestamp(), 'dd/mm/yyyy')||to_char(hr_inicio_prescricao_w, 'hh24:mi:ss'), 'dd/mm/yyyy hh24:mi:ss');
	else
		dt_prim_horario_w := to_date(to_char(clock_timestamp() + interval '1 days', 'dd/mm/yyyy')||to_char(hr_inicio_prescricao_w, 'hh24:mi:ss'), 'dd/mm/yyyy hh24:mi:ss');
	end if;
	end;
end if;
return dt_prim_horario_w;
end;
$body$
LANGUAGE PLPGSQL
