CREATE OR REPLACE FUNCTION obter_prim_hor_setor ( cd_setor_atendimento_p bigint) RETURNS timestamp AS $body$
DECLARE


hr_inicio_prescricao_w	timestamp;
dt_prim_horario_w	timestamp;	


BEGIN

if (cd_setor_atendimento_p IS NOT NULL AND cd_setor_atendimento_p::text <> '') then

select	coalesce(hr_inicio_prescricao,to_date('30/12/1899 00:00:01','dd/mm/yyyy hh24:mi:ss'))
into STRICT	hr_inicio_prescricao_w
from	setor_atendimento
where	cd_setor_atendimento = cd_setor_atendimento_p;

if (coalesce(hr_inicio_prescricao_w::text, '') = '') or (hr_inicio_prescricao_w = to_date('30/12/1899 00:00:01','dd/mm/yyyy hh24:mi:ss')) then
	--Horario de inicio da prescricao nao informado no cadastro do setor! #@#@');
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(198402);
end if;

if (to_char(hr_inicio_prescricao_w, 'hh24:mi:ss') > to_char(clock_timestamp(), 'hh24:mi:ss')) then
	dt_prim_horario_w := ESTABLISHMENT_TIMEZONE_UTILS.dateAtTime(clock_timestamp(), coalesce(to_char(hr_inicio_prescricao_w, 'hh24:mi:ss'), '00:00'));
else
	dt_prim_horario_w := ESTABLISHMENT_TIMEZONE_UTILS.dateAtTime(clock_timestamp() + interval '1 days', coalesce(to_char(hr_inicio_prescricao_w, 'hh24:mi:ss'), '00:00'));
end if;

end if;

return dt_prim_horario_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
