SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_prim_observacao (nr_sequencia_p bigint, ie_tipo_pend_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w varchar(2000);


BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (ie_opcao_p = 'Q') then

	 if (ie_tipo_pend_p = 'Q') then

		select      	max(c.ds_obs_final)
		into STRICT		ds_retorno_w
		from        	agenda_quimio		 a,
				agenda_integrada_item b,
				agenda_integrada c
		where     	a.nr_sequencia = nr_sequencia_p
		and		a.nr_seq_pend_agenda = b.nr_seq_pend_quimio
		and 		b.nr_seq_agenda_int  = c.nr_sequencia;
else
		select      	max(a.ds_observacao)
		into STRICT		ds_retorno_w
		from        	ageint_observacao_final a,
				agenda_integrada b,
				agenda_integrada_item c
		where     	a.nr_seq_ageint = b.nr_sequencia
		and	  	b.nr_sequencia = c.nr_seq_agenda_int
		and 		c.nr_seq_agequi = nr_sequencia_p
		and 		a.nr_sequencia = (SELECT 	min(x.nr_sequencia)
							 from 		ageint_observacao_final x
							where		a.nr_seq_ageint = x.nr_seq_ageint);

	end if;
end if;

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (ie_opcao_p = 'C') then

	 select      	max(a.ds_observacao)
	 into STRICT  		ds_retorno_w
	 from        	ageint_observacao_final a,
			agenda_integrada b,
			agenda_integrada_item c
	 where     	a.nr_seq_ageint = b.nr_sequencia
	 and	  	b.nr_sequencia = c.nr_seq_agenda_int
	 and 		c.nr_seq_agenda_cons = nr_sequencia_p
	 and 		a.nr_sequencia =	 (SELECT 	min(x.nr_sequencia)
						 from 		ageint_observacao_final x
						 where		a.nr_seq_ageint = x.nr_seq_ageint);

end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
