CREATE OR REPLACE FUNCTION obter_prim_prescr_atend_medic (nr_atendimento_p bigint, cd_medicamento_p bigint) RETURNS timestamp AS $body$
DECLARE


dt_prim_prescr_w timestamp;


BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (cd_medicamento_p IS NOT NULL AND cd_medicamento_p::text <> '') then

	SELECT  MIN(a.dt_prescricao)
	into STRICT    dt_prim_prescr_w
	FROM   	prescr_material b,
		prescr_medica a
	WHERE  	a.nr_prescricao  = b.nr_prescricao
	AND	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
	AND	coalesce(a.dt_suspensao::text, '') = ''
	AND     a.nr_atendimento = nr_atendimento_p
	AND     b.cd_material 	 = cd_medicamento_p;

end if;

return	dt_prim_prescr_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
