CREATE OR REPLACE FUNCTION obter_prim_setor_coleta_prescr (nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE

cd_setor_coleta_w	bigint;


BEGIN

select	coalesce(max(a.cd_setor_coleta),0)
into STRICT	cd_setor_coleta_w
from	prescr_procedimento a
where	a.nr_prescricao = nr_prescricao_p
and	a.nr_sequencia = (	SELECT	min(b.nr_sequencia)
				from	prescr_procedimento b
				where	b.nr_prescricao = nr_prescricao_p);

return	cd_setor_coleta_w;

end;
