CREATE OR REPLACE FUNCTION obter_prioridade_pep (nr_prescricao_p bigint, nr_seq_prescricao_p bigint, ds_titulo_laudo_p text) RETURNS REG_PRIOR_PROCED_EXAME.NR_SEQUENCIA%TYPE AS $body$
DECLARE


nr_seq_prioridade_w 	varchar(80);
nr_sequencia_w 	numeric(20);
nr_tmp_execucao_w	smallint;
nr_tmp_execucao_menor_w	smallint;
ie_status_execucao_w 	varchar(80);

ie_multiplo_w   smallint;


C01 CURSOR FOR
  SELECT  c.nr_sequencia
	 FROM   prescr_procedimento a,
			cpoe_procedimento b,
			protocolo_medic_proc c,
			prescr_medica d
	 WHERE  a.nr_seq_proc_cpoe = b.nr_sequencia
	 AND    a.nr_seq_proc_interno = b.nr_seq_proc_interno
	 AND    b.nr_seq_protocolo = c.nr_sequencia
	 AND    b.cd_protocolo = c.cd_protocolo
	 AND    b.nr_seq_proc_interno = c.nr_seq_proc_interno
	 AND    a.nr_prescricao = nr_prescricao_p
	 AND    d.nr_prescricao = a.NR_PRESCRICAO;


BEGIN

ie_multiplo_w := 1;

nr_seq_prioridade_w := null;
nr_tmp_execucao_menor_w := null;
nr_tmp_execucao_menor_w := 1000;


select 0
into STRICT ie_multiplo_w

where ds_titulo_laudo_p not like '%+%';

if ( ie_multiplo_w = 1) then

  open C01;
    loop
    fetch C01 into	
      nr_sequencia_w;
    EXIT WHEN NOT FOUND; /* apply on C01 */
      begin

      begin
      select  min(nr_tempo_ciencia)
      into STRICT    nr_tmp_execucao_w
      from    reg_prior_proced_exame
      where   nr_sequencia = nr_sequencia_w;
         exception
        when others then
          nr_tmp_execucao_w  := 1000;
      end;

      if (nr_tmp_execucao_w < nr_tmp_execucao_menor_w) then
          begin      
            nr_tmp_execucao_menor_w := nr_tmp_execucao_w;
            nr_seq_prioridade_w := nr_sequencia_w;

          end;
      end if;

      end;
    end loop;
    close C01;

else

  SELECT  c.NR_SEQ_REG_PRIOR_PROCED_EXAME
  into STRICT    nr_seq_prioridade_w
  FROM   prescr_procedimento a,
			cpoe_procedimento b,
			protocolo_medic_proc c,
			prescr_medica d
	 WHERE  a.nr_seq_proc_cpoe = b.nr_sequencia
	 AND    a.nr_seq_proc_interno = b.nr_seq_proc_interno
	 AND    b.nr_seq_protocolo = c.nr_sequencia
	 AND    b.cd_protocolo = c.cd_protocolo 
	 AND    b.nr_seq_proc_interno = c.nr_seq_proc_interno
	 AND    a.nr_prescricao = nr_prescricao_p
   AND    a.nr_sequencia = nr_seq_prescricao_p
	 AND    d.nr_prescricao = a.NR_PRESCRICAO;
end if;

return nr_seq_prioridade_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
