CREATE OR REPLACE FUNCTION obter_prioridade_reg_pls ( NR_SEQUENCIA_P bigint, cd_procedimento_p bigint default null, ie_opcao_p text default 'A') RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
nr_Seq_requisicao_w	bigint;
NR_SEQ_PRIORIDADE_w	bigint;


BEGIN
if (NR_SEQUENCIA_P IS NOT NULL AND NR_SEQUENCIA_P::text <> '') THEN

	if (ie_opcao_p = 'A') then

		Select  max(nr_Seq_requisicao)
		into STRICT	nr_Seq_requisicao_w
		from	pls_auditoria
		where	nr_sequencia = NR_SEQUENCIA_P;

		if ( nr_Seq_requisicao_w > 0) then

			Select  max(a.NR_SEQ_PRIORIDADE)
			into STRICT	nr_seq_prioridade_w
			from	pls_requisicao_proc a,
					pls_requisicao b
			where	a.nr_Seq_requisicao = b.nr_sequencia
			and		b.nr_sequencia = nr_Seq_requisicao_w
			and		a.cd_procedimento = cd_procedimento_p;

			if (nr_seq_prioridade_w IS NOT NULL AND nr_seq_prioridade_w::text <> '') then

				Select  obter_prioridade_regulacao(nr_seq_prioridade_w)
				into STRICT	ds_retorno_w
				;

			end if;

		end if;

	end if;


end if;

RETURN	ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
