CREATE OR REPLACE FUNCTION obter_prioridade_triagem (nr_seq_queixa_p bigint) RETURNS varchar AS $body$
DECLARE

								
ds_retorno_w	varchar(10) := 'N';
qt_reg_w		bigint;


BEGIN

select	count(*)
into STRICT 	qt_reg_w
from 	queixa_paciente_triagem;

if (qt_reg_w > 0) then

	if (nr_seq_queixa_p IS NOT NULL AND nr_seq_queixa_p::text <> '') then
	
		select 	nr_sequencia as cd
		into STRICT  	ds_retorno_w
		from 	triagem_classif_risco
		where 	nr_sequencia = (SELECT nr_seq_classif from queixa_paciente_triagem where nr_seq_queixa = nr_seq_queixa_p);
		return	ds_retorno_w;

	end if;

end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
