CREATE OR REPLACE FUNCTION obter_prior_os_usuario_sup ( nm_usuario_p text, nr_seq_ordem_p bigint) RETURNS varchar AS $body$
DECLARE


ie_prioridade_sup_w	smallint;


BEGIN

select	max(coalesce(ie_prioridade_sup,0))
into STRICT	ie_prioridade_sup_w
from	man_ordem_ativ_prev
where	nr_seq_ordem_serv = nr_seq_ordem_p
and	dt_prevista between trunc(clock_timestamp()) and fim_dia(clock_timestamp())
and 	coalesce(dt_real::text, '') = ''
