CREATE OR REPLACE FUNCTION obter_prior_os_usuario ( nm_usuario_p text, nr_seq_ordem_p bigint) RETURNS varchar AS $body$
DECLARE


ie_prioridade_desen_w	smallint;


BEGIN

SELECT	MAX(coalesce(IE_PRIORIDADE_DESEN,0))
INTO STRICT	ie_prioridade_desen_w
FROM	man_ordem_ativ_prev
WHERE	nr_seq_ordem_serv = nr_seq_ordem_p
AND	dt_prevista BETWEEN TRUNC(clock_timestamp()) AND fim_dia(clock_timestamp())
AND 	coalesce(dt_real::text, '') = ''
AND	nm_usuario_prev	= nm_usuario_p;

RETURN	ie_prioridade_desen_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
