SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_privacidade_parametros (ds_campo_p text) RETURNS varchar AS $body$
DECLARE

			
ds_retorno_w		varchar(200) := 'N';


BEGIN

    if (upper(ds_campo_p) = 'PPF') then
        begin
            select	coalesce(ie_privacidade_pf,'N')
            into STRICT	ds_retorno_w
            from	privacidade_parametros;
        exception
            when others then
             ds_retorno_w := 'N';
        end;
    elsif (upper(ds_campo_p) = 'UC') then
        begin
            select	coalesce(ie_utiliza_captcha,'N')
            into STRICT	ds_retorno_w
            from	privacidade_parametros;
        exception
            when others then
             ds_retorno_w := 'N';
        end;
    elsif (upper(ds_campo_p) = 'SP') then
        begin
            select	coalesce(ie_show_privacy,'N')
            into STRICT	ds_retorno_w
            from	privacidade_parametros;
        exception
            when others then
             ds_retorno_w := 'N';
        end;
    end if;

    return	ds_retorno_w;
	
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
