CREATE OR REPLACE FUNCTION obter_privacy_cpanel ( ie_tipo_privacy_p text, nr_seq_atributo_p bigint, cd_perfil_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1);


BEGIN

if (ie_tipo_privacy_p = 'S') then
	select	max(ie_sensitive)
	into STRICT	ds_retorno_w
	from (SELECT w.ie_sensitive,
			row_number() over (order by w.nm_usuario_regra, w.cd_perfil) nr_seq_ordem
		from	wcp_regra_atributo w
		where	w.nr_seq_atributo = nr_seq_atributo_p
		and	coalesce(w.cd_perfil, cd_perfil_p) = cd_perfil_p
		and	coalesce(w.nm_usuario_regra, nm_usuario_p) = nm_usuario_p) alias6
	where nr_seq_ordem = 1;

elsif (ie_tipo_privacy_p = 'P') then
	select	max(ie_patient_info)
	into STRICT	ds_retorno_w
	from (SELECT w.ie_patient_info,
			row_number() over (order by w.nm_usuario_regra, w.cd_perfil) nr_seq_ordem
		from	wcp_regra_atributo w
		where	w.nr_seq_atributo = nr_seq_atributo_p
		and	coalesce(w.cd_perfil, cd_perfil_p) = cd_perfil_p
		and	coalesce(w.nm_usuario_regra, nm_usuario_p) = nm_usuario_p) alias6
	where nr_seq_ordem = 1;
end if;

return	ds_retorno_w;

end;
