CREATE OR REPLACE FUNCTION obter_procedencia_agenda_exame (nr_seq_agenda_p bigint, ie_tipo_agenda_p text) RETURNS bigint AS $body$
DECLARE


ie_procedencia_w	smallint;


BEGIN
IF (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') AND (ie_tipo_agenda_p = 'E') THEN

	SELECT	MAX(cd_procedencia)
	INTO STRICT	ie_procedencia_w
