	WHERE	nr_sequencia = nr_seq_agenda_p;

END IF;

IF (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') AND (ie_tipo_agenda_p = 'C') THEN

	SELECT	MAX(cd_procedencia)
	INTO STRICT	ie_procedencia_w
