CREATE OR REPLACE FUNCTION obter_procedencia_pf (cd_pessoa_fisica_p text) RETURNS bigint AS $body$
DECLARE


nr_seq_proced_w	bigint;
cd_procedencia_w	integer;


BEGIN
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	/* obter registro */

	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_seq_proced_w
	from	pf_procedencia
	where	cd_pessoa_fisica = cd_pessoa_fisica_p;

	/* obter valor registro */

	if (nr_seq_proced_w > 0) then
		select	cd_procedencia
		into STRICT	cd_procedencia_w
		from	pf_procedencia
		where	nr_sequencia = nr_seq_proced_w;
	end if;
end if;

return cd_procedencia_w;

end;
