CREATE OR REPLACE FUNCTION obter_procedencia_setor ( cd_setor_atendimento_p bigint, cd_estabelecimento_p bigint) RETURNS bigint AS $body$
DECLARE


ds_retorno_w		integer;
cd_procedencia_w	integer;
cd_perfil_w		bigint;

C01 CURSOR FOR
	SELECT	cd_procedencia
	from	procedencia
	where	cd_setor_atendimento				= cd_setor_atendimento_p
	and	coalesce(cd_estabelecimento, cd_estabelecimento_p)	= cd_estabelecimento_p
	order by 1;

C02 CURSOR FOR
	SELECT	cd_procedencia
	from	regra_procedencia_setor
	where	coalesce(cd_setor_atendimento,cd_setor_atendimento_p) 	= cd_setor_atendimento_p
	and	coalesce(cd_estabelecimento, cd_estabelecimento_p)		= cd_estabelecimento_p
	and coalesce(cd_perfil,cd_perfil_w) = cd_perfil_w
	order by 1;



BEGIN

cd_perfil_w := obter_perfil_ativo;

open C01;
loop
fetch C01 into
	cd_procedencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ds_retorno_w	:= cd_procedencia_w;
	end;
end loop;
close C01;

if (coalesce(cd_procedencia_w::text, '') = '') then

	open C02;
	loop
	fetch C02 into
		cd_procedencia_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin
		ds_retorno_w	:= cd_procedencia_w;
		end;
	end loop;
	close C02;


end if;

return	ds_retorno_w;

end;
$body$
