CREATE OR REPLACE FUNCTION obter_procedimentos_mx (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


cd_procedimento_w	varchar(4000) 	:= '';
ds_retorno_w            	varchar(4000) 	:= '';

C01 CURSOR FOR
	SELECT  a.cd_procimento_mx,
	           c.ds_procedimento
    from 	procedimento a,
	proc_pac_descricao b,
	cat_procedimento c
    where 	b.nr_atendimento = nr_atendimento_p
    and   	a.cd_procedimento = b.cd_procedimento
    and   	a.cd_procimento_mx = c.cd_procedimento;

BEGIN
for r_C01_w in C01 loop
	begin
		if (coalesce(cd_procedimento_w::text, '') = '') then
			cd_procedimento_w := r_C01_w.cd_procimento_mx || '&' || r_C01_w.ds_procedimento;
		else
			cd_procedimento_w := cd_procedimento_w  || '#' || r_C01_w.cd_procimento_mx || '&' || r_C01_w.ds_procedimento;
               			 ds_retorno_w :=	cd_procedimento_w;
	                end if;
                end;
end loop;

return ds_retorno_W;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
