SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_procedimento_aferese ( nr_prescricao_p bigint, nr_seq_prescr_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


cd_procedimento_w	bigint;
ds_procedimento_w	varchar(255);
nr_seq_proc_interno_w	bigint;

BEGIN

SELECT	max(cd_procedimento), max(nr_seq_proc_interno)
into STRICT	cd_procedimento_w,
	nr_seq_proc_interno_w
FROM	prescr_procedimento
WHERE	nr_prescricao = nr_prescricao_p
AND	nr_sequencia = nr_seq_prescr_p;



if (ie_opcao_p = 'D') then

	if (coalesce(nr_seq_proc_interno_w,0) > 0)  then
		select	max(ds_proc_exame)
		into STRICT	ds_procedimento_w
		from	proc_interno
		where	nr_sequencia	= nr_seq_proc_interno_w
		and	ie_tipo = 'AT'
		order by ds_proc_exame;
	else
		SELECT	max(substr(a.ds_proc_exame,1,255))
		into STRICT	ds_procedimento_w
		FROM   	procedimento b,
			proc_interno a
		WHERE  	a.ie_tipo = 'AT'
		AND    	a.cd_procedimento    = b.cd_procedimento
		AND    	a.ie_origem_proced   = b.ie_origem_proced
		AND	a.cd_procedimento    = cd_procedimento_w
		ORDER BY a.ds_proc_exame;
end if;


end if;

if (ie_opcao_p = 'D') then
	return ds_procedimento_w;
else
	return	cd_procedimento_w;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
