CREATE OR REPLACE FUNCTION obter_procedimento_agenda_gv (nr_seq_agenda_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_procedimento_w	varchar(255);
cd_procedimento_w	bigint;
nr_seq_proc_interno_w	bigint;
ie_origem_proced_w	varchar(15);

BEGIN


if (ie_opcao_p = 'P') then
		select   max(cd_procedimento),
			 nr_seq_proc_interno,
			 ie_origem_proced
		into STRICT	 cd_procedimento_w,
			 nr_seq_proc_interno_w,
			 ie_origem_proced_w
		from	 agenda_paciente
		where	 nr_sequencia = nr_seq_agenda_p
		group by cd_procedimento,
			 nr_seq_proc_interno,
			 ie_origem_proced;

		if (cd_procedimento_w IS NOT NULL AND cd_procedimento_w::text <> '') then
			ds_procedimento_w := substr(obter_exame_agenda(cd_procedimento_w, ie_origem_proced_w, nr_seq_proc_interno_w),1,240);
		end if;
end if;

if (ie_opcao_p = 'PA') then
		select   max(cd_procedimento),
			 nr_seq_proc_interno,
			 ie_origem_proced
		into STRICT	 cd_procedimento_w,
			 nr_seq_proc_interno_w,
			 ie_origem_proced_w
		from 	 agenda_paciente_proc
		where	 nr_seq_agenda = nr_seq_agenda_p
		group by cd_procedimento,
			 nr_seq_proc_interno,
			 ie_origem_proced;

		if (cd_procedimento_w IS NOT NULL AND cd_procedimento_w::text <> '') then
			ds_procedimento_w := substr(obter_desc_prescr_proc(cd_procedimento_w, ie_origem_proced_w, nr_seq_proc_interno_w),1,240);
		end if;
end if;

return	ds_procedimento_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
