SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_procedimento_associado ( nr_sequencia_proc_p bigint, nr_prescricao_p bigint) RETURNS bigint AS $body$
DECLARE


/* Retorna 0 (Zero) caso o procedimento ainda não tenha sido executado,
   Retorna o nr_sequencia da Procedimento_paciente caso já tenha sido executado*/
nr_sequencia_w			bigint:= 0;
qt_executado_w			integer:= 0;


BEGIN

select 	count(*)
into STRICT	qt_executado_w
from 	procedimento_paciente
where 	nr_prescricao = nr_prescricao_p
and 	nr_sequencia_prescricao = nr_sequencia_proc_p;

if (qt_executado_w > 0) then
	select 	max(nr_sequencia)
	into STRICT	nr_sequencia_w
	from 	procedimento_paciente
	where 	nr_prescricao = nr_prescricao_p
	and 	nr_sequencia_prescricao = nr_sequencia_proc_p;
else
	nr_sequencia_w:= 0;
end if;

return nr_sequencia_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
