CREATE OR REPLACE FUNCTION obter_procedimento_guia (nr_interno_conta_p bigint, cd_autorizacao_p text) RETURNS varchar AS $body$
DECLARE


cd_procedimento_w	bigint;
ie_origem_proced_w	bigint;
ds_procedimento_w	varchar(1000);
nr_seq_proc_interno_w   bigint;
vl_procedimento_w	double precision;

c01 CURSOR FOR
	SELECT	coalesce(cd_procedimento,0),
		ie_origem_proced,
		coalesce(nr_seq_proc_interno,0),
		vl_procedimento
	from	procedimento_paciente
	where 	nr_interno_conta = nr_interno_conta_p
	and 	nr_doc_convenio	 = cd_autorizacao_p
	order by vl_procedimento;



BEGIN

cd_procedimento_w := 0;

open	c01;
	loop
	fetch	c01
	into	cd_procedimento_w,
		ie_origem_proced_w,
		nr_seq_proc_interno_w,
		vl_procedimento_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
	end loop;
close 	c01;

if (cd_procedimento_w > 0) then
	ds_procedimento_W:= obter_desc_prescr_proc(cd_procedimento_w, ie_origem_proced_w,nr_seq_proc_interno_w);
else
	ds_procedimento_w := '';
end if;

RETURN ds_procedimento_w;

END;
