SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_procedimento_idade (nr_seq_procedimento_p bigint, nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_aplica_setor_w 		varchar(1) := 'S';
cd_pessoa_fisica_w		varchar(10);
qt_idade_w				double precision;
qt_idade_dias_w			double precision;
cd_setor_atendimento_w	integer;
qt_reg_w				bigint;


BEGIN

if (nr_atendimento_p > 0) and (nr_seq_procedimento_p > 0) then

	cd_pessoa_fisica_w := OBTER_PESSOA_ATENDIMENTO(nr_atendimento_p,'C');
	cd_setor_atendimento_w := Obter_Setor_Atendimento(nr_atendimento_p);
	qt_idade_dias_w := coalesce(trunc(Obter_Idade_PF(cd_pessoa_fisica_w,clock_timestamp(),'DIA')),0);
	qt_idade_w	:= coalesce(trunc(Obter_Idade_PF(cd_pessoa_fisica_w,ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(clock_timestamp()),'A')),0);
	
	select	count(1)
	into STRICT	qt_reg_w
	from	regra_idade_dor
	where	nr_seq_procedimento = nr_seq_procedimento_p;

	if (qt_reg_w > 0) then
		select	coalesce(MAX(IE_APLICA_SETOR),'N')
		into STRICT	ie_aplica_setor_w
		from	regra_idade_dor
		where	nr_seq_procedimento = nr_seq_procedimento_p
		and		qt_idade_w between coalesce(QT_IDADE_MIN,0) and  coalesce(QT_IDADE_MAX,999)
		and 	qt_idade_dias_w between coalesce(QT_IDADE_MIN_DIAS,0) and coalesce(QT_IDADE_MAX_DIAS,999999999)
		and		coalesce(cd_setor_atendimento,cd_setor_atendimento_w) = cd_setor_atendimento_w;
	end if;
	
end if;

return ie_aplica_setor_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
