SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_procedimento_nfse ( nr_seq_nota_fiscal_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


/* ie_opcao_p
P - procedimento
O - ie_origem_proced
*/
cd_procedimento_w		bigint;
nr_seq_proc_interno_w	bigint;
ie_origem_proced_w		bigint;
ie_orig_proced_w		bigint;


BEGIN

select	max(b.cd_procedimento),
		max(b.nr_seq_proc_interno)
into STRICT	cd_procedimento_w,
		nr_seq_proc_interno_w
from	nota_fiscal a,
	nota_fiscal_item b
where	a.nr_sequencia = b.nr_sequencia
and	a.nr_sequencia = nr_seq_nota_fiscal_p
and	coalesce(b.cd_material::text, '') = ''
order by
	b.cd_procedimento;

if (coalesce(cd_procedimento_w::text, '') = '') then
begin

	select	cd_procedimento,
			ie_origem_proced
	into STRICT	cd_procedimento_w,
			ie_orig_proced_w
	from	proc_interno
	where	nr_sequencia = nr_seq_proc_interno_w;

	end;
end if;


if (ie_opcao_p = 'P') then
	return	cd_procedimento_w;
elsif (ie_opcao_p = 'O') then

	select	coalesce(max(b.ie_origem_proced),0)
	into STRICT	ie_origem_proced_w
	from	nota_fiscal a,
		nota_fiscal_item b
	where	a.nr_sequencia = b.nr_sequencia
	and	a.nr_sequencia = nr_seq_nota_fiscal_p
	and	b.cd_procedimento = cd_procedimento_w
	and	coalesce(b.cd_material::text, '') = ''
	order by
		b.cd_procedimento;

	if (ie_origem_proced_w = 0) then
		begin
		if (nr_seq_proc_interno_w IS NOT NULL AND nr_seq_proc_interno_w::text <> '') then
			begin
			ie_origem_proced_w	:= ie_orig_proced_w;
			end;
		else
			begin

			select 	max(ie_origem_proced)
			into STRICT	ie_origem_proced_w
			from 	procedimento
			where	cd_procedimento	= cd_procedimento_w;

			end;
		end if;
		end;
	end if;

	return	ie_origem_proced_w;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
