CREATE OR REPLACE FUNCTION obter_procedimento_orcamento ( nr_sequencia_orc_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/* IE_OPCAO_P
	MC - Código do proc de maior valor
	MD - Descrição do proc de maior valor
*/
cd_procedimento_w	bigint;
vl_procedimento_W	double precision;
ie_origem_proced_W	bigint;
vl_proc_maiorval_w	double precision := 0;
ds_retorno_w		varchar(255);
ds_procedimento_w	PROCEDIMENTO.DS_PROCEDIMENTO%type;

C01 CURSOR FOR
	SELECT	cd_procedimento,
		vl_procedimento,
		ie_origem_proced,
		obter_descricao_procedimento(cd_procedimento, ie_origem_proced)
	from	orcamento_paciente_proc
	where	nr_sequencia_orcamento = nr_Sequencia_orc_p;


BEGIN

open C01;
loop fetch C01 into
	cd_procedimento_w,
	vl_procedimento_w,
	ie_origem_proced_w,
	ds_procedimento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (ie_opcao_p = 'MC') then
		if (vl_procedimento_w > vl_proc_maiorval_W) then
			ds_retorno_w		:= cd_procedimento_w;
			vl_proc_maiorval_w	:= vl_procedimento_w;
		end if;
	elsif (ie_opcao_p = 'MD') then
		if (vl_procedimento_w > vl_proc_maiorval_w) then
			ds_retorno_w		:= ds_procedimento_w;
			vl_proc_maiorval_w	:= vl_procedimento_w;
		end if;
	end if;
	end;
end loop;
close c01;
if (coalesce(ds_retorno_W::text, '') = '') then
	select	max(cd_procedimento),
		max(ie_origem_proced)
	into STRICT	cd_procedimento_w,
		ie_origem_proced_w
	from	orcamento_paciente_proc
	where	nr_sequencia_orcamento	= nr_sequencia_orc_p;

	if (ie_opcao_p = 'MC') then
		ds_retorno_w	:= cd_procedimento_w;
	else
		select	obter_descricao_procedimento(cd_procedimento_w, ie_origem_proced_w)
		into STRICT	ds_retorno_w
		;
	end if;
end if;

RETURN ds_retorno_w;
END;
$body$
