CREATE OR REPLACE FUNCTION obter_procedimento_processo (nr_seq_pepo_p bigint) RETURNS varchar AS $body$
DECLARE


ds_procedimento_w		PROCEDIMENTO.DS_PROCEDIMENTO%type := null;

BEGIN

select	max(substr(obter_exame_agenda(cd_procedimento, ie_origem_proced, nr_seq_proc_interno),1,255))
into STRICT	ds_procedimento_w
from	pepo_cirurgia_proc
where 	nr_seq_pepo = nr_seq_pepo_p
and		ie_proc_principal = 'S';

if (coalesce(ds_procedimento_w::text, '') = '') then
	select	max(substr(obter_exame_agenda(cd_procedimento, ie_origem_proced, nr_seq_proc_interno),1,255))
	into STRICT	ds_procedimento_w
	from	pepo_cirurgia_proc
	where 	nr_seq_pepo = nr_seq_pepo_p;
end if;

return	ds_procedimento_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
