CREATE OR REPLACE FUNCTION obter_procedi_agenda_atend (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(2000);


BEGIN

SELECT	obter_select_concatenado_bv('select 	substr(obter_desc_prescr_proc(a.cd_procedimento,a.ie_origem_proced, a.nr_seq_proc_interno),1,240)
				  from    	prescr_procedimento a
				  where	a.nr_prescricao =  (select 	max(x.nr_prescricao)
						              from   	prescr_medica x
						              where  	x.nr_atendimento = :nr_atendimento)
						              order by 	1', 'nr_atendimento='||nr_atendimento_p||';',', ')
into STRICT	ds_retorno_w
;

return	ds_retorno_w;

end;
