CREATE OR REPLACE FUNCTION obter_proceds_laudo (nr_laudo_p bigint) RETURNS varchar AS $body$
DECLARE

ds_retorno_w	varchar(4000);
ds_procedimento_w	varchar(255);


C01 CURSOR FOR
	SELECT 	distinct
			b.ds_procedimento
	from  	procedimento b,
			procedimento_paciente a
	where 	a.nr_laudo           = nr_laudo_p
	and   	a.cd_procedimento    = b.cd_procedimento
	and   	a.ie_origem_proced   = b.ie_origem_proced
	order by b.ds_procedimento;


BEGIN
open C01;
loop
fetch C01 into
	ds_procedimento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (coalesce(ds_retorno_w::text, '') = '') then
		ds_retorno_w	:= ds_procedimento_w;
	else
		ds_retorno_w	:= substr(ds_retorno_w||','||ds_procedimento_w,1,4000);
	end if;
	end;
end loop;
close C01;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
