CREATE OR REPLACE FUNCTION obter_proced_adic_lab_agecons (nr_seq_agenda_p bigint) RETURNS varchar AS $body$
DECLARE


ds_procedimentos_w	varchar(4000);
ds_proced_w		varchar(260);

c01 CURSOR FOR
SELECT	substr(obter_desc_exame(nr_seq_exame),1,90)
from	agenda_consulta_proc a
where	a.nr_seq_agenda = nr_seq_agenda_p
and	(nr_seq_exame IS NOT NULL AND nr_seq_exame::text <> '');


BEGIN
if (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') then
	open c01;
	loop
	fetch c01	into
			ds_proced_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		if (coalesce(length(ds_procedimentos_w),0) < 3930) then
			ds_procedimentos_w := ds_procedimentos_w || ds_proced_w || '; ';
		end if;
		end;
	end loop;
	close c01;
end if;

return ds_procedimentos_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
