CREATE OR REPLACE FUNCTION obter_proced_adic_prev (nr_cirurgia_p bigint, nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE



cd_procedimento_w		bigint;
ie_origem_proced_w		bigint;
ds_procedimento_w		varchar(255);
ds_retorno_w			varchar(20000);

c01 CURSOR FOR
SELECT	substr(obter_desc_prescr_proc(cd_procedimento,ie_origem_proced,nr_seq_proc_interno),1,240)
from	prescr_procedimento
where	nr_prescricao		= nr_prescricao_p
and	cd_procedimento	<> cd_procedimento_w;


BEGIN

select	max(cd_procedimento_princ),
	max(ie_origem_proced)
into STRICT	cd_procedimento_w,
	ie_origem_proced_w
from	cirurgia
where	nr_cirurgia	= nr_cirurgia_p;

open C01;
loop
fetch C01 into
	ds_procedimento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ds_retorno_w	:= ds_retorno_w ||ds_procedimento_w ||chr(13) ||chr(10);
	end;
end loop;
close C01;

return substr(ds_retorno_w,1,2000);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
