CREATE OR REPLACE FUNCTION obter_proced_autor (nr_atendimento_p bigint, nr_seq_autorizacao_p bigint, ie_proced_p bigint) RETURNS varchar AS $body$
DECLARE


cd_procedimento_w		varchar(8)	:= ' ';

/* Function feita para a OS 38138
  Ie_proced_p:
	Informar qual procedimento deve buscar, 1,2..
*/
BEGIN

select	max(CASE WHEN rownum=ie_proced_p THEN substr(to_char(cd_procedimento),1,8)  ELSE null END )
into STRICT	cd_procedimento_w
from	procedimento_autorizado
where	nr_atendimento		= nr_atendimento_p
and	nr_seq_autorizacao	= nr_seq_autorizacao_p;


return cd_procedimento_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
