CREATE OR REPLACE FUNCTION obter_proced_conversao_conv (cd_convenio_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/*
C - Código;
D - Descrição;
*/
cd_proc_convenio_w	varchar(20);
ds_proc_convenio_w	varchar(100);
ds_retorno_w		varchar(100);
ie_escape_w		varchar(1)	:= 'N';


BEGIN

if (cd_convenio_p IS NOT NULL AND cd_convenio_p::text <> '') and (cd_procedimento_p IS NOT NULL AND cd_procedimento_p::text <> '') and (ie_origem_proced_p IS NOT NULL AND ie_origem_proced_p::text <> '') then
	begin
	select	max(cd_proc_convenio),
		max(ds_proc_convenio)
	into STRICT	cd_proc_convenio_w,
		ds_proc_convenio_w
	from	conversao_proc_convenio
	where	cd_convenio		= cd_convenio_p
	and	cd_procedimento		= cd_procedimento_p
	and	ie_origem_proced	= ie_origem_proced_p;
	exception
		when others then
		ie_escape_w	:= 'S';
	end;

	if (ie_escape_w <> 'S') then

		if (ie_opcao_p = 'C') then
			ds_retorno_w	:= cd_proc_convenio_w;
		else
			ds_retorno_w	:= ds_proc_convenio_w;
		end if;
	end if;

end if;

RETURN	ds_retorno_w;

END	;
