CREATE OR REPLACE FUNCTION obter_proced_turno_agenda (cd_agenda_p bigint, dt_agenda_p timestamp) RETURNS bigint AS $body$
DECLARE


cd_procedimento_w	varchar(10);
nr_seq_proc_interno_w	bigint;
ie_dia_semana_w		integer;


BEGIN

select	obter_cod_dia_semana(dt_agenda_p)
into STRICT	ie_dia_semana_w
;

select	max(nr_seq_proc_interno)
into STRICT	nr_seq_proc_interno_w
from	agenda_horario
where	cd_agenda = cd_agenda_p
and	((dt_dia_semana = ie_dia_semana_w) or ((dt_dia_semana = 9) and (ie_dia_Semana_w not in (7,1))))
and	((coalesce(dt_final_vigencia::text, '') = '') or (dt_final_vigencia >= trunc(dt_agenda_p)))
and	((coalesce(dt_inicio_vigencia::text, '') = '') or (dt_inicio_vigencia <= trunc(dt_agenda_p)))
and 	hr_inicial < hr_final
and	coalesce(nr_minuto_intervalo,0) > 0
and	to_date(trunc(dt_agenda_p, 'dd') || ' ' || to_char(dt_agenda_p, 'hh24:mi:ss'), 'dd/mm/yyyy hh24:mi:ss') between
			to_date(trunc(dt_agenda_p, 'dd') || ' ' || to_char(hr_inicial, 'hh24:mi:ss'), 'dd/mm/yyyy hh24:mi:ss') and
			to_date(trunc(dt_agenda_p, 'dd') || ' ' || to_char(hr_final, 'hh24:mi:ss'), 'dd/mm/yyyy hh24:mi:ss');

return	nr_seq_proc_interno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
