CREATE OR REPLACE FUNCTION obter_processo_adicionar ( nr_atendimento_p bigint, dt_horario_p timestamp, cd_local_estoque_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_adicionar_w	bigint := 0;


BEGIN
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (dt_horario_p IS NOT NULL AND dt_horario_p::text <> '') then

	select	coalesce(max(a.nr_sequencia),0)
	into STRICT	nr_seq_adicionar_w
	from	adep_processo	a
	where	a.nr_atendimento 		= nr_atendimento_p
	and		a.dt_horario_processo	= dt_horario_p
	and		a.ie_status_processo	= 'G'
	and		coalesce(a.cd_local_estoque,0)	= coalesce(cd_local_estoque_p,0)
	and		coalesce(a.nr_seq_solucao::text, '') = ''
	and 	coalesce(dt_suspensao::text, '') = ''
	and		exists (SELECT	1
					from	prescr_mat_hor	x
					where	a.nr_sequencia	= x.nr_seq_processo
					and		x.ie_agrupador in (1,5));
end if;

return nr_seq_adicionar_w;

end;
$body$
