CREATE OR REPLACE FUNCTION obter_procsolic_aihunif_padrao ( nr_interno_conta_p bigint) RETURNS bigint AS $body$
DECLARE

 
 
nr_atendimento_w	bigint;
nr_aih_w		bigint;
cd_procedimento_w	bigint	:= 0;


BEGIN 
 
select	nr_atendimento 
into STRICT	nr_atendimento_w 
from	conta_paciente 
where	nr_interno_conta	= nr_interno_conta_p;
 
select	coalesce(max(nr_aih),0) nr_aih 
into STRICT	nr_aih_w 
from	sus_aih_unif 
where 	nr_interno_conta 	= nr_interno_conta_p;
 
if (nr_aih_w	= 0) then 
	select	coalesce(max(nr_aih),0) 
	into STRICT	nr_aih_w 
	from	sus_aih_unif 
	where	nr_atendimento	= nr_atendimento_w;
end if;
 
 
if (nr_aih_w	> 0) then 
	begin 
	select	coalesce(max(cd_procedimento_solic),0)	 
	into STRICT	cd_procedimento_w 
	from	sus_laudo_paciente 
	where	nr_aih			= nr_aih_w 
	and	nr_atendimento		= nr_atendimento_w 
	and	ie_tipo_laudo_sus	= 1;
 
	if (cd_procedimento_w	= 0) then 
		begin 
		select	coalesce(max(cd_procedimento_solic),0) 
		into STRICT	cd_procedimento_w 
		from	sus_laudo_paciente 
		where	nr_aih			= nr_aih_w 
		and	nr_atendimento	 	= nr_atendimento_w 
		and	ie_tipo_laudo_sus	= 0;		
		 
		if (cd_procedimento_w	= 0) then 
			begin 
			select	coalesce(max(cd_procedimento_solic),0) 
			into STRICT	cd_procedimento_w 
			from	sus_aih_unif 
			where	nr_aih	=	nr_aih_w;
			end;
		end if;
		end;
	end if;
	end;
else 
	begin 
	select	coalesce(max(cd_procedimento_solic),0)	 
	into STRICT	cd_procedimento_w 
	from	sus_laudo_paciente 
	where	nr_atendimento		= nr_atendimento_w 
	and	ie_tipo_laudo_sus	= 1;
 
	if (cd_procedimento_w	= 0) then 
		begin 
		select	coalesce(max(cd_procedimento_solic),0) 
		into STRICT	cd_procedimento_w 
		from	sus_laudo_paciente 
		where	nr_atendimento	 	= nr_atendimento_w 
		and	ie_tipo_laudo_sus	= 0;
		end;
	end if;		
	end;
end if;
 
RETURN	cd_procedimento_w;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
