CREATE OR REPLACE FUNCTION obter_proc_aprov ( nr_sequencia_p bigint) RETURNS bigint AS $body$
DECLARE


			
cd_processo_aprov_w	processo_aprov_resp.cd_processo_aprov%type;
			

BEGIN

select	max(cd_processo_aprov)
into STRICT	cd_processo_aprov_w
from	processo_aprov_compra
where	nr_seq_proc_aprov   = nr_sequencia_p;


if (coalesce(cd_processo_aprov_w::text, '') = '') then
	select	max(cd_processo_aprov)
	into STRICT	cd_processo_aprov_w
	from	processo_aprov_resp
	where	nr_sequencia   = nr_sequencia_p;
end if;

return	cd_processo_aprov_w;

end;
