SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_proc_atend_futuro ( cd_pessoa_fisica_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/* IE_OPCAO_P
D - Descrição
*/
ds_retorno_w		varchar(2000);
ds_procedimento_w		varchar(255);
nr_seq_proc_interno_w	bigint;
cd_procedimento_w	bigint;

C01 CURSOR FOR
	SELECT	substr(obter_exame_agenda(b.cd_procedimento, b.ie_origem_proced, b.nr_seq_proc_interno),1,240),
		b.cd_procedimento,
		b.nr_seq_proc_interno
	from   	atend_pac_futuro a,
		atend_pac_proced_previsto b
	where  	a.nr_sequencia = b.nr_seq_atend_futuro
	and	coalesce(a.nr_atendimento::text, '') = ''
	and    	a.cd_pessoa_fisica = cd_pessoa_fisica_p
	order by ie_proc_principal desc, 1 asc;


BEGIN

open C01;
loop
fetch C01 into
	ds_procedimento_w,
	cd_procedimento_w,
	nr_seq_proc_interno_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (ie_opcao_p = 'D') then
		ds_retorno_w := substr(ds_retorno_w||ds_procedimento_w||', ',1,2000);

	end if;
	end;
end loop;
close C01;

ds_retorno_w := substr(ds_retorno_w,1,length(ds_retorno_w)-2);

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
